import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
	data: any;
	filteredData: any;
	searchQuery: string = '';

	constructor(private apiService: ApiService) {}

	ngOnInit(): void {
		this.apiService.get().subscribe(
			(data) => {
				this.data = data;
				this.filteredData = this.data;
			},
			(error) => {
				console.error(error);
			}
		);
	}

	searchBooks(searchQuery: string): void {
		if (searchQuery.trim() === '') {
		  this.filteredData = this.data;
		} 
		// else {
		//   this.filteredData = this.data.filter(
		// 	(item) =>
		// 	  item.title.toLowerCase().includes(searchQuery.toLowerCase())
		//   );
		// }
	  }
	generateImageUrl(itemId: number): string {
		itemId++;
		return `https://picsum.photos/seed/${itemId}/300/400`;
	  }
}
<div>
  <input type="text" [(ngModel)]="searchQuery" placeholder="Search by title" />
  <button (click)="searchBooks(searchQuery)">Search</button>
</div>

<div class="library-catalog">
    <div class="book-card" *ngFor="let item of filteredData; let i = index">
      <div class="book-image">
        <img [src]="generateImageUrl(i)" alt="Book Image" />
      </div>
      <h2 class="book-title">{{item.title}}</h2>
      <p class="book-author">{{item.author}}</p>
      <p class="book-description">{{ item.description }}</p>
      <button class="check-availability-button">Check Availability</button>
    </div>
</div>
