import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
	data: any;
	constructor(private apiService: ApiService) {}

	ngOnInit(): void {
		this.apiService.get().subscribe(
			(data) => {
				this.data = data;
			},
			(error) => {
				console.error(error);
			}
		);
	}
	generateImageUrl(itemId: string): string {
		itemId++;
		return `https://picsum.photos/seed/${itemId}/300/400`;
	  }
}
<div class="product-card" *ngFor="let item of data; let i = index">
    <div class="product-image">
      <img [src]="generateImageUrl()" alt="Product Image" />
    </div>
    <div class="product-details">
      <h2 class="product-title">{{ item.title }}</h2>
      <p class="product-author">{{ item.author }}</p>
      <p class="product-description">{{ item.description }}</p>
      <button class="add-to-cart">Check Availability</button>
    </div>
</div>
